$(document).ready(function(){centerSlider();fadeSlider();slider4();tubs();scrollToTop()});function centerSlider(){if($('.center-slider')===null){return}
$('.center-slider').slick({centerMode:!0,centerPadding:'60px',slidesToShow:3,responsive:[{breakpoint:768,settings:{arrows:!0,centerMode:!0,centerPadding:'40px',slidesToShow:3,}},{breakpoint:480,settings:{arrows:!0,centerMode:!0,centerPadding:'40px',slidesToShow:1}}]})}
function fadeSlider(){if($('.fade-slider')===null){return}
$('.fade-slider').slick({dots:!1,infinite:!0,speed:500,fade:!0,cssEase:'linear',autoplay:!0,autoplaySpeed:5000})}
function slider4(){if($('.slider4')===null){return}
$('.slider4').slick()}
function tubs(){var trailerDescriptionTabs=$('#trailer-description');if((trailerDescriptionTabs===null)){return}
trailerDescriptionTabs.tab()}
function scrollToTop(){if(($(window).height()+100)<$(document).height()){$('#top-link-block').removeClass('hidden').affix({offset:{top:100}})}}